import React, { useEffect, useState } from "react";
import Prismic from "prismic-javascript";

const apiEndpoint = process.env.PRISMIC_WEB_API;
const accessToken = process.env.PRISMIC_API_KEY;

const Client = Prismic.client(apiEndpoint, { accessToken });

export default function data(props) {
  const [doc, setDocData] = useState(null);
  const [value, setValue] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const response = await Client.query(
        Prismic.Predicates.any("document.type", [
          "homepage",
          "constants",
          "contact_me",
          "header",
          "homepage0",
          "my_articles",
          "testimonials",
          "what_i_did",
        ])
      );
      if (response) {
        setDocData(response.results);
      }
    };
    fetchData();
  }, []);

  // // useEffect(() => {
  // //   doc && setValue(extractData(props))
  // // }, [doc])

  // const extractData = (props) => doc.filter((item) => item.type == props)

  // if(props === "homepage"){
  //   return doc && setValue(extractData(props))
  // }

  // if(props === "homepage0"){
  //   return doc && setValue(extractData(props))
  // }

  return doc;
}
